schema @expand(template: "Topology") { query: Ignored }

type Campus implements Topology {
	id: 			ID!
	campusName: 	String
	buildings: 		[Building]
	companies:		[Company]
}

type Building implements Referenceable {
	id:			ID!
	buildingName: 		String
	rooms: 		[Room]
}

type Room implements Referenceable {
	id:			ID!
	roomName: 	String
	area:		Float
	# devices: 	[Device]
}

type Company {
	name:		String
	units:		[Referenceable]
}

{
	"schemaId": "{{namespace}}:campus-schema:1",
	"policyId": "{{namespace}}:topologyPolicy",
	"_content": {
		"query":  "mutation { create(topology: { id: \"c1\", campusName: \"Berlin\", buildings: [{ id: \"b1\", buildingName: \"Building 1\", rooms: [{ id: \"b1r1\", roomName: \"Room 1\", area: 25.5 } ] }, { id: \"b2\", buildingName: \"Building 2\", rooms: [{ id: \"b2r1\", roomName: \"Room 1\", area: 38.5 } ] } ], companies: [{ name: \"Acme\", units: [{ id: \"b1\" }, { id: \"b2r1\" } ] } ] }) { campusName } }"
	}
}

{
	topology {
		campusName,
		buildings {
			buildingName,
			rooms {
				roomName
			}
		},
		companies {
			name,
			units {
				id
			}
		}
	}
}

{
	topology {
		companies {
			name,
			units {
				...on Building {
					id,
					buildingName
				}
				...on Room {
					id,
					roomName
				}
			}
		}
	}
}

{
