schema @expand(template: "ResourceAwareTopology") { query: Ignored }

type Campus implements Topology {
	id: 			ID!
	campusName: 	String
	buildings: 		[Building]
	companies:		[Company]
}

type Building implements Referenceable {
	id:				ID!
	buildingName:	String
	rooms: 			[Room]
}

type Room implements Referenceable {
	id:			ID!
	roomName: 	String
	area:		Float
	lamps: 		[Lamp]
}

type Company {
	name:		String
	units:		[Referenceable]
}

interface LampThing @resource(protocol: "com.bosch.iot.things:features-protocol:0.0.1") {
	id:			ID!
}

type Lamp implements LampThing {
	id:					ID!	
	status:				Status
	powerConsumption:	Float
}

enum Status {
	ON
	OFF
	BROKEN
}

{
	"query": "mutation ($campus: CampusUpdateType) { update(topology: $campus) { outcome, resourceUpdates { id }, resourceFailures { id } } }",
	"variables": {
		"campus": {
			"id": "c1",
			"campusName": "Berlin",
			"buildings": [{
					"id": "b1",
					"buildingName": "Building 1",
					"rooms": [{
							"id": "b1r1",
							"roomName": "Room 1",
							"area": "25.5",
							"lamps": {
								"id": "{{namespace}}:lamp01/LampFeature",
								"status": "ON",
								"powerConsumption": "150.6"
							}
						}
					]
				}
			]
		}
	}
}

{
	topology {
		campusName,
		buildings {
			buildingName,
			rooms {
				roomName,
				lamps {
					id,
					status,
					powerConsumption
				}
			}
		}
	}
}
